async function () {
  let userInfo = await appsmith.store.user;
  if (userInfo) {
    const agencyId = JSON.stringify(userInfo.agency_id);
    await find_service_allowed.run({
      agencyId
    }).then(res => form.serviceAllowed = JSON.stringify(res[0].service_type_codes)).catch(e => {
      showAlert("Can not retrieve service type code. " + e.message, 'error');
    });
    showAlert(form.serviceAllowed);
    form.isWifiAllowed = form.serviceAllowed.includes('光wifi');
    form.isCardAllowed = form.serviceAllowed.includes('カードcredit_card');
    form.isUtilitiesAllowed = form.serviceAllowed.includes('電気ガスutility');
    form.isWifiAllowed ? service_wifi_cb.setVisibility(true) : service_wifi_cb.setVisibility(false);
    form.isCardAllowed ? service_card_cb.setVisibility(true) : service_card_cb.setVisibility(false);
    form.isUtilitiesAllowed ? service_utilities_cb.setVisibility(true) : service_utilities_cb.setVisibility(false);
  } else {
    showAlert("Can not retrieve user info. ", 'error');
  }
}

async function () {
  if (!loop_check_token.intervalId) {
    const process = async () => {
      const token = appsmith.store.token;
      console.log('token', token);
      loop_check_token.checkValidTokenInDb();
      const tokenInDb = await check_token_exist.run({
        token
      });
      console.log('token in db', tokenInDb);
      const tokenCountInDb = tokenInDb[0]['count'];
      console.log('tokenCountInDb', tokenCountInDb);
      if (tokenCountInDb === 0 && appsmith.mode !== 'EDIT') {
        navigateTo('Login');
        console.log('navigate to login');
      }
      console.log('token still valid');
    };
    loop_check_token.intervalId = setInterval(process, 5000);
  }
}
