async () => {
  let treeData = [];
  let yearsMap = {};
  const responseData = await get_months_have_billing.run().catch(e => showAlert(' fail to get months have billing', 'error'));
  responseData.forEach(record => {
    if (!yearsMap[record.year]) {
      yearsMap[record.year] = {
        label: record.year.toString(),
        value: record.year.toString(),
        children: []
      };
      treeData.push(yearsMap[record.year]);
    }
    yearsMap[record.year].children.push({
      label: record.month,
      value: record.month
    });
  });
  return treeData;
}

async function () {
  if (!authentication.intervalId) {
    const process = async () => {
      try {
        const token = appsmith.store.token;
        console.log('token', token);
        authentication.checkValidTokenInDb();
        const tokenInDb = await check_token_exist.run({
          token
        });
        console.log('token in db', tokenInDb);
        const tokenCountInDb = tokenInDb[0]['count'];
        console.log('tokenCountInDb', tokenCountInDb);
        if (tokenCountInDb === 0) {
          authentication.logout();
          console.log('navigate to login');
        }
        console.log('token still valid');
      } catch (error) {
        showAlert('Something wrong, please login again! \n' + error.message, 'error');
      }
    };
    authentication.intervalId = setInterval(process, 5000);
  }
}
